<script lang="ts" setup>
const props = defineProps<{
  options: {
    label: string
    value: string
  }[]
  value: string
}>()

const emit = defineEmits<{
  change: [value: string]
}>()
</script>

<template>
  <select
    :value="props.value"
    @change="emit('change', $event.target.value)"
  >
    <option
      v-for="option in props.options"
      :key="option.value"
      :value="option.value"
    >
      {{ option.label }}
    </option>
  </select>
</template>

